mapper.insert:

	select nextval('cuckoo.syspl_affiche_seq')

insert into cuckoo.syspl_affiche
(
	affiche_id, affiche_title, affiche_invalidate, affiche_pulish, affiche_content
)
values (
	#{afficheId}, #{afficheTitle}, #{afficheInvalidate}, #{affichePulish}, #{afficheContent}
)

mapper.select:
select * from cuckoo.syspl_affiche
and affiche_title like #{afficheTitle}
and affiche_pulish = #{affichePulish}
order by affiche_id desc
