mapper.insert:

	select nextval('cuckoo.syspl_module_memu_seq')

insert into cuckoo.syspl_module_memu
(
	module_id, mod_parent_id, mod_name, mod_en_id, mod_img_cls, mod_level, 
	mod_order, belong_to_sys, mod_page_type, memo, status, creator, create_date
)
values (
	#{moduleId}, #{moduleMemu.moduleId}, #{modName}, #{modEnId}, #{modImgCls}, #{modLevel}, 
	#{modOrder}, #{belongToSys}, #{modPageType}, #{memo}, #{status}, #{creator}, #{createDate}
)

mapper.select:
select * from cuckoo.syspl_module_memu where module_id != 0
and mod_name like #{modName}
and mod_en_id like #{modEnId}
and module_id in
#{id}
order by module_id desc
