mapper.insert:

	select nextval('cuckoo.uum_org_role_ref_seq')

insert into cuckoo.uum_org_role_ref
(
	org_role_id, role_id, org_id
)
values (
	#{orgRoleId}, #{role.roleId}, #{organ.orgId}
)

mapper.select:
select t1.org_role_id as org_role_id, t1.role_id as role_role_id, t2.role_name as role_role_name, t2.status as role_status, t2.memo as role_memo, t2.creator as role_creator, t2.create_date as role_create_date, t2.role_level as role_role_level from cuckoo.uum_org_role_ref t1 left join cuckoo.uum_role t2 on t1.role_id = t2.role_id where t1.org_id = #{orgId}
and t2.role_name like #{roleName}
order by org_role_id desc
